package com.example.bmail.Activities;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.provider.MediaStore;
import android.util.Log;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;

import com.example.bmail.R;
import com.example.bmail.ViewModels.SignupViewModel;

public class SignupActivity extends AppCompatActivity {
    private SignupViewModel viewModel;
    private static final int PICK_IMAGE_REQUEST = 1;
    private ImageView profileImageView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_signup);
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        viewModel = new SignupViewModel();
        profileImageView = findViewById(R.id.profileImageView);

        Button signupBtn = findViewById(R.id.signupButton);
        signupBtn.setOnClickListener(view -> handleSignupButtonClick());

        Button choosePhotoBtn = findViewById(R.id.choosePhotoButton);
        choosePhotoBtn.setOnClickListener(view -> handleChoosePhotoButtonClick());
    }

    // ...existing code...

    @Override
    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        if (requestCode == PICK_IMAGE_REQUEST && resultCode == RESULT_OK && data != null
                && data.getData() != null) {
            Uri selectedImageUri = data.getData();
            Log.i("foo", "Selected Image URI: " + selectedImageUri.toString());

            // Display the selected image in the circular ImageView
            profileImageView.setImageURI(selectedImageUri);
        }
    }

    // ...existing code...
}

package com.example.bmail.Entities;

import java.util.List;

public class MailRequest {
    private String title;
    private String body;
    private String from;
    private List<String> to;
    private Boolean draft;

    public MailRequest(String title, String body, String from, List<String> to, Boolean draft) {
        this.title = title;
        this.body = body;
        this.from = from;
        this.to = to;
        this.draft = draft;
    }

    // Getters and setters
    public String getTitle() { return title; }
    public void setTitle(String title) { this.title = title; }

    public String getBody() { return body; }
    public void setBody(String body) { this.body = body; }

    public String getFrom() { return from; }
    public void setFrom(String from) { this.from = from; }

    public List<String> getTo() { return to; }
    public void setTo(List<String> to) { this.to = to; }

    public Boolean getDraft() { return draft; }
    public void setDraft(Boolean draft) { this.draft = draft; }
}

